// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Index extends Component {
  state = {
    account: ''
  }
  render() {
    web3.eth.getAccounts().then( account => {
      if(account[0] !== ''){
      this.setState({ account: account[0] });
    }
    });
    return(
      <div className="container">
        <nav>
          <h2>Certify</h2>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">News</a></li>
              <li><a href="/verify">Contact</a></li>
            </ul>
          </div>
        </nav>

        <div className="body">

          <h4 className="address">{this.state.account}</h4>

        </div>


        <style jsx>{`
            .container {
              margin: 0 50px;
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: 0;
              padding: 0;
              background-color: #fff;
            }

            .body {
              margin-top: 100px;
            }

            nav {
              background-color: #16a085;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h2 {
              font-family: Montserrat;
              color: white;
              margin: 15px 50px;
            }

            ul {
              list-style-type: none;
              margin: 0;
              padding: 0;
              overflow: hidden;
              position: absolute;
              top: 0;
              right: 0;
            }

            li {
              float: left;
            }

            li a {
              display: block;
              padding: 8px;
              margin-right: 40px;
              font-family: Montserrat;
              color: #fff;
              text-decoration: none;
              margin-top: 10px;
            }

            .address {
              font-family: Montserrat;
              background-color: #2ecc71;
              height: 15px;
              padding: 10px;
              border-radius: 8px;
              color: #fff;
              font-weight: 400;
              position: absolute;
              top: 60px;
              right: 15px;
            }

            h4 {
              text-align: centre;
            }

          `}</style>
      </div>
    )
  }
}

export default Index;
