// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Index extends Component {
  state = {
    account: ''
  }
  render() {
    web3.eth.getAccounts().then( account => {
      if(account[0] !== ''){
      this.setState({ account: account[0] });
    }
    });
    return(
      <div className="container">
        <nav>
          <h2>Certify</h2>
          <h4 className="address">{this.state.account}</h4>
        </nav>

        <div className="body">
          <h1>Certify</h1>
          <p>jsd kdc sis cdsha svs vahva s</p>
        </div>


        <style jsx>{`
            .container {
              margin: 0 50px;
            }

            nav {
              background-color: #333;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h2 {
              font-family: Montserrat;
              color: white;
              margin: 15px 50px;
            }

            .address {
              font-family: Montserrat;
              color: white;
              font-weight: 400;
              position: absolute;
              top: 0;
              right: 0;
            }

            .body {
              margin-top: 100px;
            }
            p {
              color: blue;
            }
          `}</style>
      </div>
    )
  }
}

export default Index;
