// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Create extends Component {
  state = {
    account: '',
    org: '',
    name: '',
    course: '',
    hash: '',
    desc: '',
    date: '',
    aadhar: '',
    uni: '',
    block: 'block0'
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const hash = md5(this.state.course + this.state.name + this.state.organisation);
    this.setState({ hash });
    try {
      const accounts = await web3.eth.getAccounts();
      await instance
        .methods.generateCertificate(this.state.account, this.state.aadhar, this.state.course, hash)
        .send({ gas: '1000000', from: accounts[0]});
    } catch(err) {
      console.log(err);
    }
    // data upload finishes...
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();

    today = mm + '/' + dd + '/' + yyyy;
    this.setState({ date: today });
    console.log(today);
    this.setState({ block: "block1" });

  };

  render() {
      web3.eth.getAccounts().then( account => {
        if(account[0] !== ''){
        this.setState({ account: account[0] });
      } else {
        this.setState({ account: 'Login to your Metamask' })
      }
      });

    return(
      <div className="container">

        <nav>
          <h2>Certify</h2>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">Create</a></li>
              <li><a href="/verify">Verify</a></li>
            </ul>
          </div>

          <h4 className="address">{this.state.account}</h4>
        </nav>


        <div className="body">
        <form onSubmit={this.onSubmit}>


          <div>
            <h4>Issuer</h4>
            <input
              className="input"
              value={this.state.account}
            />
          </div>

          <div>
            <h4>Aadhar</h4>
            <input
              className="input"
              placeholder="Full Name..."
              value={this.state.aahar}
              onChange={event => this.setState({ aadhar: event.target.value })}
            />
          </div>

          <div>
            <h4>Course ID</h4>
            <input
              className="input"
              placeholder="Course ID..."
              value={this.state.course}
              onChange={event => this.setState({ course: event.target.value })}
            />
          </div>

          <div>
            <h4>University</h4>
            <input
              className="input"
              placeholder="Course ID..."
              value={this.state.uni}
              onChange={event => this.setState({ uni: event.target.value })}
            />
          </div>

          <div>
            <h4>Description</h4>
            <textarea
              className="input"
              placeholder="Course ID..."
              value={this.state.desc}
              onChange={event => this.setState({ desc: event.target.value })}
            >
            </textarea>
          </div>

          <button className="btn">Set</button>
          <h4>{ this.state.hash }</h4>
        </form>
        </div>

        <div className={this.state.block}>
          <div className="certificate">
            <h4>{this.state.address}</h4>
            <h4>{this.state.aadhar}</h4>
            <h4>{this.state.course}</h4>
            <h4>{this.state.uni}</h4>
            <h4>{this.state.desc}</h4>
            <h4>{this.state.hash}</h4>
        </div>
      </div>

        <style jsx>{`

            form {
              margin-left: 40%;
              margin-top: 10%;
            }

            .input {
              border-radius: 0;
              border: 0;
              padding: 0;

              border-radius: 8px;
              height: 20px;
              width: 600px;
              font-family: Montserrat;
              font-size: 24px;
              padding: 10px 60px;
            }

            btn {

            }

            .certificate {
              background-color: #2bcbba;
              padding: 50px 500px;
              width: 500px;
              height: 800px;
              margin: 200px;
              margin-top: 5%;
              border-radius: 12px;
            }

            .block1 {
              display: block;
            }
            .block0 {
              display: none;
            }
            .container {
              margin: 0 50px;
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: 0;
              padding: 0;
              background-color: #d1d8e0;
            }

            .body {
              margin-top: 100px;
            }

            nav {
              background-color: #16a085;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h2 {
              font-family: Montserrat;
              color: white;
              margin: 15px 50px;
            }

            ul {
              list-style-type: none;
              margin: 0;
              padding: 0;
              overflow: hidden;
              position: absolute;
              top: 0;
              right: 0;
            }

            li {
              float: left;
            }

            li a {
              display: block;
              padding: 8px;
              margin-right: 40px;
              font-family: Montserrat;
              color: #fff;
              text-decoration: none;
              margin-top: 10px;
            }

            .address {
              font-family: Montserrat;
              background-color: #2ecc71;
              height: 15px;
              padding: 10px;
              border-radius: 8px;
              color: #fff;
              font-weight: 400;
              position: absolute;
              top: 60px;
              right: 15px;
            }
          `}</style>

      </div>
    )
  }
}

export default Create;
