// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Verify extends Component {
  state = {
    account: 'Metamask not connected!',
    org: '',
    name: '',
    course: '',
    hash: '',
    fetch: ''
  }
  async componentDidMount() {
    const accounts = web3.eth.getAccounts();
      if(accounts[0] !== ''){
      this.setState({ account: accounts[0] });
      console.log(this.state.account);
    } else {
      this.setState({ account: 'No Metamask' });
      console.log(this.state.account);
    }
  }
  onFetch = async (e) => {
    e.preventDefault();

    try {
      const getbykey = await instance.methods.getValue(this.state.hash)
        .call().then(result => {
          console.log(result[0], result[1], result[2], result[3]);
        });
        console.log(getbykey);
    } catch(err) {
      console.log(err);
    }
  };

  render() {

    return(
      <div>
        <h4>{this.state.account}</h4>
        <form onSubmit={this.onFetch}>

          <div>
            <label>Enter the hash</label>
            <input
              value={this.state.hash}
              onChange={event => this.setState({ hash: event.target.value })}
            />
          </div>

          <button>Get</button>
          <h2>{this.state.fetch}</h2>
        </form>
      </div>
    )
  }
}

export default Verify;
