// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Create extends Component {
  state = {
    account: '',
    org: '',
    name: '',
    course: '',
    hash: '',
    block: 'block0'
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const hash = md5(this.state.course + this.state.name + this.state.organisation);
    this.setState({ hash });
    try {
      const accounts = await web3.eth.getAccounts();
      await instance
        .methods.generateCertificate(this.state.org, this.state.name, this.state.course, hash)
        .send({ gas: '1000000', from: accounts[0]});
    } catch(err) {
      console.log(err);
    }
  };

  render() {
      web3.eth.getAccounts().then( account => {
        if(account[0] !== ''){
        this.setState({ account: account[0] });
      } else {
        this.setState({ account: 'Login to your Metamask' })
      }
      });
    return(
      <div className="container">

        <nav>
          <h2>Certify</h2>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">Create</a></li>
              <li><a href="/verify">Verify</a></li>
            </ul>
          </div>

          <h4 className="address">{this.state.account}</h4>
        </nav>


        <div className="body">
        <form onSubmit={this.onSubmit}>

          <div>
            <label>Enter the org</label>
            <input
              value={this.state.org}
              onChange={event => this.setState({ org: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the name</label>
            <input
              value={this.state.name}
              onChange={event => this.setState({ name: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the course</label>
            <input
              value={this.state.course}
              onChange={event => this.setState({ course: event.target.value })}
            />
          </div>

          <button>Set</button>
          <h4>{ this.state.hash }</h4>
        </form>
        </div>

        <div>

        </div>

        <style jsx>{`
            .container {
              margin: 0 50px;
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: 0;
              padding: 0;
              background-color: #000;
            }

            .body {
              margin-top: 100px;
            }

            nav {
              background-color: #16a085;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h2 {
              font-family: Montserrat;
              color: white;
              margin: 15px 50px;
            }

            ul {
              list-style-type: none;
              margin: 0;
              padding: 0;
              overflow: hidden;
              position: absolute;
              top: 0;
              right: 0;
            }

            li {
              float: left;
            }

            li a {
              display: block;
              padding: 8px;
              margin-right: 40px;
              font-family: Montserrat;
              color: #fff;
              text-decoration: none;
              margin-top: 10px;
            }

            .address {
              font-family: Montserrat;
              background-color: #2ecc71;
              height: 15px;
              padding: 10px;
              border-radius: 8px;
              color: #fff;
              font-weight: 400;
              position: absolute;
              top: 60px;
              right: 15px;
            }
          `}</style>

      </div>
    )
  }
}

export default Create;
