// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';

class Index extends Component {
  state = {
    org: '',
    name: '',
    course: '',
    hash: '',
    fetch: ''
  }

  onSubmit = async (e) => {
    e.preventDefault();

    try {
      const accounts = await web3.eth.getAccounts();
      await instance
        .methods.generateCertificate(this.state.org, this.state.name, this.state.course, this.state.hash)
        .send({ gas: '1000000', from: accounts[0]});
    } catch(err) {
      console.log(err);
    }
  };

  onFetch = async (e) => {
    e.preventDefault();

    try {
      const getbykey = await instance.methods.getValue(this.state.fetch)
        .call().then(result => {
          console.log(result[0]);
        });
    } catch(err) {
      console.log(err);
    }
  };

  render() {
      web3.eth.getAccounts().then( account => {
        console.log(account);
      });
    return(
      <div>
        <form onSubmit={this.onSubmit}>

          <div>
            <label>Enter the value</label>
            <input
              value={this.state.value}
              onChange={event => this.setState({ value: event.target.value })}
            />
          </div>

          <button>Set</button>
        </form>

        <form onSubmit={this.onFetch}>

          <div>
            <label>Enter the address</label>
            <input
              value={this.state.hash}
              onChange={event => this.setState({ hash: event.target.value })}
            />
          </div>

          <button>Get</button>
          <h2>{this.state.getValue}</h2>
        </form>
      </div>
    )
  }
}

export default Index;
