// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Index extends Component {
  state = {
    account: ''
  }
  render() {
    web3.eth.getAccounts().then( account => {
      if(account[0] !== ''){
      this.setState({ account: account[0] });
    }
    });
    return(
      <div className="container">
        <nav>
          <h5>Certify</h5>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">Create</a></li>
              <li><a href="/verify">Verify</a></li>
            </ul>
          </div>

          <h4 className="address">{this.state.account}</h4>
        </nav>

        <div className="body">

          <h1>Blockchain POC</h1>
          <h2>Approach</h2>
          <p>We are using blockchain technology to store and verify credentials.</p>
          <p> Treditionally, central servers are used which is costly. Since data has to be stored
          on the database which costs to the company that holds our certificate. And cerdentials are prone to security issues.

          </p>


        </div>


        <style jsx>{`

            .container {
              margin: 0 50px;
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: 0;
              padding: 0;
              background-color: #fff;
            }

            .body {
              margin-top: 100px;
            }

            nav {
              background-color: #4b6584;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h5 {
              font-family: Montserrat;
              font-size: 30px;
              color: white;
              margin: 15px 50px;
            }

            ul {
              list-style-type: none;
              margin: 0;
              padding: 0;
              overflow: hidden;
              position: absolute;
              top: 0;
              right: 0;
            }

            li {
              float: left;
            }

            li a {
              display: block;
              padding: 8px;
              margin-right: 40px;
              font-family: Montserrat;
              color: #fff;
              text-decoration: none;
              margin-top: 10px;
            }

            h1 {
              font-family: Montserrat;
              font-size: 24px;
              color: #000;
              margin-left: 500px;
              width: 500px;
              margin-top: 150px;
            }

            h2 {
              font-family: Montserrat;
              font-size: 18px;
              color: #000;
              margin-left: 200px;
              width: 800px;
            }

            p {
              font-family: Montserrat;
              font-size: 16px;
              color: #000;
              margin-left: 200px;
              width: 1000px;
            }

            .address {
              font-family: Montserrat;
              background-color: #4b6584;
              height: 15px;
              padding: 20px;
              border-radius: 8px;
              color: #fff;
              font-weight: 600;
              position: absolute;
              top: 60px;
              right: 15px;
            }
          `}</style>
      </div>
    )
  }
}

export default Index;
