// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';
// 098bc8ecc4b72c6ea5d165a654bafd5f
class Verify extends Component {
  state = {
    account: '',
    org: 'KIET',
    name: 'Lav Singh',
    course: 'Udemy',
    hash: '',
    hashId: '098bc8ecc4b72c6ea5d165a654bafd5f',
    block: 'block2'
  }

  onFetch = async (e) => {
    e.preventDefault();

    try {
      const getbykey = await instance.methods.getValue(this.state.hash)
        .call().then(result => {
          console.log(result[0], result[1], result[2], result[3]);
          this.setState({org: result[0]});
          this.setState({name: result[1]});
          this.setState({course: result[2]});
          this.setState({hashId: result[3]});
        });
        console.log(getbykey);
        this.setState({ block: 'block1' });
    } catch(err) {
      console.log(err);
    }
  };

  render() {
    web3.eth.getAccounts().then( account => {
      if(account[0] !== ''){
      this.setState({ account: account[0] });
    } else {
      this.setState({ account: 'Login to your Metamask' })
    }
    });
    return(
      <div className="container">

        <nav>
          <h2>Certify</h2>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">Create</a></li>
              <li><a href="/verify">Verify</a></li>
            </ul>
          </div>

          <h4 className="address">{this.state.account}</h4>
        </nav>

        <div className="body">

        <form onSubmit={this.onFetch}>

          <div>
            <input
              className="input"
              value={this.state.hash}
              placeholder="Enter the hash..."
              onChange={event => this.setState({ hash: event.target.value })}
            />
          </div>

          <button className="btn">Verify</button>
          <h2>{this.state.fetch}</h2>
        </form>

          <div className={this.state.block}>
            <div className="certificate">
              <h4>{this.state.org}</h4>
              <h4>{this.state.name}</h4>
              <h4>{this.state.course}</h4>
              <h4>{this.state.hashId}</h4>
            </div>
          </div>
        </div>

        <style jsx>{`
            form {
              display: flex;
              margin-top: 15%;
              margin-left: 28%;
            }
            h5 {
              font-family: Montserrat;
              font-size: 18px;
            }
            .btn {
              height: 60px;
              width: 150px;
              border: 0;
              border-radius: 0 8px 8px 0;
              font-family: Montserrat;
              font-size: 24px;
            }
            .input {
              border: 0;
              border-radius: 8px 0 0 8px;
              height: 40px;
              width: 600px;
              font-family: Montserrat;
              font-size: 24px;
              padding: 10px 60px;
            }
            .certificate {
              background-color: #2bcbba;
              padding: 50px 500px;
              width: 500px;
              margin: 200px;
              margin-top: 5%;
              border-radius: 12px;
            }
            h4 {
              font-family: Montserrat;
              font-size: 18px;
            }
            .block1 {
              display: block;
            }
            .block2 {
              display: none;
            }

            .container {
              margin: 0 50px;
              position: absolute;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: 0;
              padding: 0;
              background-color: #d1d8e0;
            }

            .body {
              margin-top: 100px;
            }

            nav {
              background-color: #16a085;
              position: absolute;
              right: 0;
              left: 0;
              top: 0;
              height: 60px;
            }

            nav h2 {
              font-family: Montserrat;
              color: white;
              margin: 15px 50px;
            }

            ul {
              list-style-type: none;
              margin: 0;
              padding: 0;
              overflow: hidden;
              position: absolute;
              top: 0;
              right: 0;
            }

            li {
              float: left;
            }

            li a {
              display: block;
              padding: 8px;
              margin-right: 40px;
              font-family: Montserrat;
              color: #fff;
              text-decoration: none;
              margin-top: 10px;
            }

            .address {
              font-family: Montserrat;
              background-color: #2ecc71;
              height: 15px;
              padding: 10px;
              border-radius: 8px;
              color: #fff;
              font-weight: 400;
              position: absolute;
              top: 60px;
              right: 15px;
            }
          `}</style>

      </div>
    )
  }
}

export default Verify;
