// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Create extends Component {
  state = {
    account: '',
    org: '',
    name: '',
    course: '',
    hash: ''
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const hash = md5(this.state.course + this.state.name + this.state.organisation);
    this.setState({ hash });
    try {
      const accounts = await web3.eth.getAccounts();
      await instance
        .methods.generateCertificate(this.state.org, this.state.name, this.state.course, hash)
        .send({ gas: '1000000', from: accounts[0]});
    } catch(err) {
      console.log(err);
    }
  };

  render() {
      web3.eth.getAccounts().then( account => {
        if(account[0] !== ''){
        this.setState({ account: account[0] });
      } else {
        this.setState({ account: 'Login to your Metamask' })
      }
      });
    return(
      <div>

        <nav>
          <h2>Certify</h2>

          <div className="nav-item">
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/about">About</a></li>
              <li><a href="/create">News</a></li>
              <li><a href="/verify">Contact</a></li>
            </ul>
          </div>

          <h4 className="address">{this.state.account}</h4>
        </nav>

        <div className="body">
        <form onSubmit={this.onSubmit}>

          <div>
            <label>Enter the org</label>
            <input
              value={this.state.org}
              onChange={event => this.setState({ org: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the name</label>
            <input
              value={this.state.name}
              onChange={event => this.setState({ name: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the course</label>
            <input
              value={this.state.course}
              onChange={event => this.setState({ course: event.target.value })}
            />
          </div>

          <button>Set</button>
          <h4>{ this.state.hash }</h4>
        </form>
        </div>
      </div>
    )
  }
}

export default Create;
