// CLIENT SIDE
import React, { Component } from 'react';
import instance from '../ethereum/instance';
import web3 from '../ethereum/web3';
import md5 from 'md5';

class Index extends Component {
  state = {
    org: '',
    name: '',
    course: '',
    hash: '',
    fetch: ''
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const hash = md5(this.state.course + this.state.name);
    console.log(hash);
    try {
      const accounts = await web3.eth.getAccounts();
      await instance
        .methods.generateCertificate(this.state.org, this.state.name, this.state.course, hash)
        .send({ gas: '1000000', from: accounts[0]});
    } catch(err) {
      console.log(err);
    }
  };

  onFetch = async (e) => {
    e.preventDefault();

    try {
      const getbykey = await instance.methods.getValue(this.state.hash)
        .call().then(result => {
          console.log(result[0], result[1], result[2], result[3]);
        });
        console.log(getbykey);
    } catch(err) {
      console.log(err);
    }
  };

  render() {
      web3.eth.getAccounts().then( account => {
        console.log(account);
      });
    return(
      <div>
        <form onSubmit={this.onSubmit}>

          <div>
            <label>Enter the org</label>
            <input
              value={this.state.org}
              onChange={event => this.setState({ org: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the name</label>
            <input
              value={this.state.name}
              onChange={event => this.setState({ name: event.target.value })}
            />
          </div>

          <div>
            <label>Enter the course</label>
            <input
              value={this.state.course}
              onChange={event => this.setState({ course: event.target.value })}
            />
          </div>

          <button>Set</button>
        </form>

        <form onSubmit={this.onFetch}>

          <div>
            <label>Enter the hash</label>
            <input
              value={this.state.hash}
              onChange={event => this.setState({ hash: event.target.value })}
            />
          </div>

          <button>Get</button>
          <h2>{this.state.fetch}</h2>
        </form>
      </div>
    )
  }
}

export default Index;
